
//https://api.nutritionix.com/v1_1/item?upc=49000036756&appId=[YOURID]&appKey=[YOURKEY];
//curl -v  -X GET "https://api.nutritionix.com/v1_1/item?upc=52200004265&appId=e859ad7e&appKey=c6e37a4aaa1592dc5cc3dea8675ff078"
exports.upcDecoder = function(upcCode,callback) {
	console.log("in upcDecoder func");
	var baseURL = 'https://api.nutritionix.com/v1_1/item?upc=';
	var appId = 'e859ad7e';
	var appKey = 'c6e37a4aaa1592dc5cc3dea8675ff078';
	var url = baseURL + upcCode + '&appId='+ appId+"&appKey="+appKey

	$.ajax({
			type : 'GET',
			crossDomain: true,
			url: url, 
			cache: false,
			dataType: 'json', 
			success:function (data) {
				console.log("in success call of ajax method for nutritionix and data is "+data);
				callback({'response':"success", 'res':true});
			}
		});
}